mapper.select:
SELECT * 
FROM member 
where member_id = #{member_id} and member_pw = #{member_pw}

SELECT 	member_name, member_phone, member_address 
FROM 	member 
where 	member_id = #{member_id}
and		member_pw = #{member_pw}

SELECT 	member_id, member_name
FROM 	member 
WHERE	member_id = #{member_id}
AND		member_name= #{member_name}

SELECT  member_id, to_char(member_create,'yyyy-MM-DD')AS MEMBER_CREATE
FROM    member
WHERE   member_name = #{member_name}
AND		member_phone = #{member_phone}

mapper.update:
UPDATE 	member 
SET 	member_pw = #{member_pw} 
WHERE 	member_id = #{member_id} 
AND 	member_name = #{member_name}
AND		member_phone = #{member_phone}

mapper.select:
select member_address
from member 
where member_no = #{member_no}

mapper.update:
update member
set
member_money = nvl(member_money, 0) + #{member_money}
where
member_no = #{member_no}

mapper.select:
select member_money
from member
where member_no = #{member_no}
